import{_ as p,r as l,o as S,c as d,a as t,b as a,e as i,w as n,f as u}from"./app-fda9dad6.js";const m={};function y(c,e){const s=l("Tabs");return S(),d("div",null,[e[4]||(e[4]=t("h1",{id:"enterotypes-of-human-gut-mycobiome",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#enterotypes-of-human-gut-mycobiome","aria-hidden":"true"},"#"),a(" Enterotypes of human gut mycobiome")],-1)),e[5]||(e[5]=t("h2",{id:"summary",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#summary","aria-hidden":"true"},"#"),a(" Summary")],-1)),e[6]||(e[6]=t("p",null,"人体肠道微生物组的真菌成分，也被称为真菌组，在肠道生态和人体健康中扮演着重要角色。然而，肠道真菌组的整体结构以及个体间真菌组成的差异大部分仍然是未知的。在这项研究中，我们从包括中国在内的三个大陆的16个队列中收集了总共3363个真菌测序样本，其中包括来自中国的572个新样本。",-1)),i(s,{id:"9",data:[{id:"comment"}]},{title0:n(({value:o,isActive:r})=>e[0]||(e[0]=[a("comment")])),tab0:n(({value:o,isActive:r})=>e[1]||(e[1]=[t("p",null,"该研究调查了人体肠道真菌组的结构和个体间差异，共采集了来自全球16个队列的3363个样本，其中包括中国的572个新样本。",-1),t("p",null,"该研究使用来自16个队列的3363个样本进行ITS（内转录间隔序列）分析，识别和表征了四种肠型的真菌组。这些肠型在不同人群和地理位置之间表现出稳定性，并与细菌组的肠型呈显著相关。特别是，研究发现真菌组肠型表现出明显的年龄偏好，以念珠菌为主导的肠型（即Can型肠型）在老年人群中富集，与受损肠道屏障相关的多种疾病的风险增加有关。此外，双向中介分析揭示了与Can型肠型相关的真菌贡献的有氧呼吸途径可能介导了受损肠道屏障与衰老之间的关联。",-1)])),_:1}),e[7]||(e[7]=u('<h2 id="results" tabindex="-1"><a class="header-anchor" href="#results" aria-hidden="true">#</a> Results</h2><h3 id="landscape-of-huamn-gut-mycobiome-composition-and-diversity" tabindex="-1"><a class="header-anchor" href="#landscape-of-huamn-gut-mycobiome-composition-and-diversity" aria-hidden="true">#</a> Landscape of huamn gut mycobiome composition and diversity</h3><p>为了了解人类肠道真菌的多样性和组成，研究收集了来自15个已发表项目的ITS测序数据，并招募了572名中国参与者进行ITS1测序分析。总计3363个样本来自三大洲（欧洲、北美和亚洲）的16个队列，包括ITS1和ITS2测序数据。</p><p>该研究通过分析3363个样本的合并数据集，发现了人类肠道真菌组的多样性和组成。在德国和中国人群中，随着样本数量的增加，检测到的真菌属数量显著增加，尤其是在中国队列中超过其他队列。然而，即使合并所有数据，观察到的真菌属数量仍低于饱和水平，暗示需要增加样本量来全面了解肠道真菌的多样性。在属水平上，酿酒酵母属和念珠菌属是最丰富的，这两者在其他人体部位也是常见的共生真菌，表明它们可能与人体有着平衡的共生关系。</p><ul><li><p><strong>肠道真菌组成和多样性差异：</strong> 不同人群的肠道真菌组成和多样性存在显著差异，可能受到地理和测序方法等生物技术因素的影响。</p></li><li><p><strong>地理因素影响：</strong> 地理位置被发现可以解释肠道真菌组成3%的变异，不同大陆存在特定真菌类群的不同丰度。欧洲人群以酿酒酵母和青霉属为主，而亚洲人群富含念珠菌属，北美人群相对富含念珠菌属。</p></li><li><p><strong>地理位置和真菌多样性关系：</strong> 不同地理位置之间存在真菌多样性的变异，欧洲人群相对显示较低的真菌多样性。</p></li><li><p><strong>ITS1和ITS2测序差异：</strong> 基于ITS1测序的样本显示更高的多样性，且约38%的真菌类群对ITS1或ITS2中的其中一种是唯一的，约45%的真菌属可通过两种方法检测到。</p></li><li><p><strong>肠道真菌组与细菌组比较：</strong> 肠道真菌组相对于细菌组表现出更低的Shannon多样性和更高的个体间差异，与之前的研究一致，表明肠道真菌组更为个体特异。</p></li><li><p><strong>真菌与细菌相互关系：</strong> 发现真菌和细菌群落之间存在正相关关系，以及两者的α-多样性指数之间存在显著正相关，暗示肠道内可能存在两个界的互利相互作用。</p></li></ul><h3 id="enterotype-of-human-gut-mycobiome" tabindex="-1"><a class="header-anchor" href="#enterotype-of-human-gut-mycobiome" aria-hidden="true">#</a> Enterotype of human gut mycobiome</h3><p>人类肠道真菌群落的肠型研究</p><p>为了研究人类肠道真菌群落的整体结构和组成模式，我们将3363个样本的属水平真菌组成分成不同的群体，即肠型（详见“材料和方法”部分）。聚类分析显示，无论是ITS1还是ITS2的组合数据集都形成了四个明显的群集（图2a、b，图S5a），在不同的分类水平上进行的聚类也得到了类似的结果，例如，在家族水平上的肠型与属水平的肠型高度一致（调整过的兰德指数&gt;0.5，图S5e；请参阅补充说明），在其他分类水平上最佳的聚类数可能会发生变化（图S4f）。我们进一步对随机下采样的数据集进行了肠型分析，以评估从数据集中删除样本对整体聚类行为的影响。即使在删除了一半样本后，肠型聚类结果仍然保持不变（图S5b、c；请参阅补充说明），进一步证明了真菌肠型群的稳健性。</p><p>这三个真菌肠型在ITS1和ITS2测序数据集中均存在，其中酵母菌属（主要是酿酒酵母菌S. cerevisiae）、念珠菌属（主要是白念珠菌C. albicans）和曲霉属是最丰富的属，分别被定义为Sacc_type、Can_type和Asp_type（图S6a）。除了这三个肠型，我们在ITS1和ITS2中还观察到了第四个肠型（图2a）。然而，在ITS1中，第四个肠型由未分类的子囊菌门主导（在15.1%的ITS1样本中存在），而在ITS2中，它由未分类的酵母目主导（在5.5%的ITS2样本中存在）。ITS1和ITS2之间第四个肠型的差异可以归因于ITS1和ITS2对不同引物靶向区域的选择[35]，我们发现Ascomycota.sp和Saccharomycetales.sp在ITS1和ITS2测序数据集中分别富集（Wilcoxon秩和检验，p &lt;0.05，图S7）。在合并的数据集（ITS1和ITS2）上进行的层次聚类显示，这两个肠型可以被分组在一起，表明这两个肠型具有相似的结构（图S5d）。因此，我们在此将第四个肠型定义为Asc_type。</p>',9)),i(s,{id:"75",data:[{id:"s comment"}]},{title0:n(({value:o,isActive:r})=>e[2]||(e[2]=[a("s comment")])),tab0:n(({value:o,isActive:r})=>e[3]||(e[3]=[t("p",null,"该研究通过对3363个样本进行真菌组成的属水平分析，发现了人类肠道真菌群落的四种肠型。这些肠型在ITS1和ITS2测序数据集中均存在，分别由酵母菌属（Sacc_type）、念珠菌属（Can_type）、曲霉属（Asp_type）以及未分类的子囊菌门或酵母目（Asc_type）主导。进一步的聚类分析表明，这些肠型具有稳健性，即使在删除了一半样本后，聚类结果仍然保持不变。这些研究结果有助于更深入地了解人类肠道真菌群落的多样性和结构。",-1)])),_:1}),e[8]||(e[8]=t("p",null,"我们通过在ITS1和ITS2的组合数据集之间使用LASSO逻辑回归模型进行跨数据集验证分析，进一步验证了肠型的稳健性（详见“材料和方法”部分）。首先，模型高预测准确率（图2b，图S8）支持真菌肠型的稳健性。在没有这些肠型主导属的情况下，我们在交叉验证中也获得了良好的性能，揭示了肠型能够独立于主导属对整体真菌群落结构进行表征的能力（图2b，图S8）。此外，肠型特定真菌属的一致富集趋势表明，来自不同群体的真菌肠型具有相似的真菌组成模式（图2c）。",-1)),e[9]||(e[9]=t("p",null,"然后，我们研究了真菌肠型的地理和生态特征。在不同人群中，我们发现在欧洲人群中，Can_type肠型较不常见（ITS1: p=4.67e-14，几率比：0.20；ITS2: p=3.92e-09，几率比：0.44；Fisher确切性检验），而在北美人群中，Sacc_type肠型相对较少见（ITS1: p<2.2e-16，几率比：0.04；ITS2: p=1.8e-02，几率比：0.67；Fisher确切性检验）。这种差异可能部分归因于欧洲人群中白念珠菌的显著减少和北美人群中酿酒酵母菌的减少（Wilcoxon秩和检验，p<0.05；图S1a）。此外，我们观察到Sacc_type和Can_type的真菌多样性最低（Wilcoxon秩和检验，p<0.05；图S6b），并且真菌α多样性指数与它们各自主导属的丰度之间存在强烈而负相关关系（p<2.2e-16，Pearson相关系数< -0.3）。",-1)),e[10]||(e[10]=t("p",null,"此外，我们利用ITS1进行真菌分析和用于细菌分析的基因组学数据，探讨了真菌和细菌肠型之间的关系，因为这两种数据类型都在CHGM队列中可用（详见“材料和方法”部分）。通过基因组学数据进行属水平的细菌分析，我们识别出四个细菌肠型（图S9），分别由Bacteroides（在两个细菌肠型中分别占20.2%和37.4%，标注为prok_bac_E1和prok_bac_E2），Prevotella（在prok_bac_E3细菌肠型中占42.5%的丰度；尤其是种类P. copri）和Enterobacteriaceae（在prok_bac_E4细菌肠型中占34.9%的丰度）主导。这些观察结果与先前在亚洲人群中报告的结果一致[15, 36]。此外，我们观察到真菌肠型和细菌肠型之间存在显著的相关性（p=9.6e-03，χ2=21.8，卡方检验；图2d）。例如，Can_type真菌肠型在prok_bac_E1细菌肠型中富集（p=3.6e-03，几率比：2.41，Fisher确切性检验），在prok_bac_E3细菌肠型中减少（p=0.024，几率比：0.49，Fisher确切性检验）。我们还观察到Asp_type真菌肠型在prok_bac_E2细菌肠型中有一定富集趋势，而Asc_type真菌肠型在prok_bac_E4中富集（p=0.05，几率比=1.56，Fisher确切性检验）。与其他研究的一致结果一起（p=3.4e-07，χ2=40.6，卡方检验；图S10），这些证据表明真菌和细菌群落之间存在显著的相关性。",-1)),e[11]||(e[11]=t("h2",{id:"methods",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#methods","aria-hidden":"true"},"#"),a(" Methods")],-1)),e[12]||(e[12]=t("h3",{id:"fungal-enterotype-clustering",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#fungal-enterotype-clustering","aria-hidden":"true"},"#"),a(" fungal enterotype clustering")],-1)),e[13]||(e[13]=t("p",null,"通过使用分割周围中位数（PAM）聚类方法 [75] 对ITS1和ITS2扩增的粪便样本分别进行真菌肠型聚类，方法与先前用于细菌肠型发现的描述相同[13,14]。简而言之，样本根据基于属水平的样本间Bray-Curtis距离进行分组，其中还考虑了其他三个广泛使用的距离矩阵，包括Jaccard、Kulcxynski和Jensen-Shannon距离（JSD），以验证真菌肠型的稳健性（图S4a）。通过轮廓指数确定了最佳簇的数量。每个肠型的主导属被确定为在该肠型中相对丰度最高的属。",-1)),e[14]||(e[14]=t("p",null,"我们进一步验证了从ITS1和ITS2组合真菌数据集中分别获得的真菌肠型的结构相似性。具体而言，我们在ITS1和ITS2数据集之间执行了交叉数据集验证，其中一个数据集用于训练LASSO逻辑回归模型 [76] 以预测另一个数据集中的真菌肠型。为了确定真菌肠型是否能够反映整体群落结构，而不仅仅是主导属的差异，我们进一步从数据中移除了主导属Candida、Saccharomyces、Aspergillus、Saccharomyces sp和Ascomycota sp，然后重新执行了交叉验证。",-1))])}const I=p(m,[["render",y],["__file","FungiEnterotypes.html.vue"]]);export{I as default};
